mapper.insert:
INSERT INTO `hotlist`(company,pn,mfg,pkge,qty,price,dtcd,qlty,dtime,buylink,hdate,uid) VALUES
(SELECT id FROM `pntype` WHERE `name` LIKE concat('%',#{intList}))
#{intList}

mapper.select:
select h.*,q.name qltyName from hotlist h,qltytype q where h.qlty=q.id
AND uid = #{condition.uid}
AND company like concat('%', #{condition.hotstk.company} ,'%')
AND pn like concat('%', #{condition.hotstk.pn} ,'%')
order by h.hdate desc limit #{pageOffset},#{pageSize}
