mapper.insert:
INSERT INTO `pnlist`(ptype,pn,des,mfg,pkge,pack,mpq,price,spec,datalink) VALUES
(SELECT id FROM `pntype` WHERE `name` LIKE concat('%',#{intList}))
#{intList}

mapper.select:
SELECT * FROM hotlist
WHERE pn=#{pn}
AND mfg=#{mfg}
AND price = (SELECT MIN(price) FROM hotlist
WHERE pn=#{pn}
AND mfg=#{mfg}  )

select pid,ptype,pn,des,mfg,pkge,pack,mpq,
price,spec speck,datalink from pnlist
where pid=#{pid}
